import { Link } from "react-router-dom";
import BookShelf from "./BookShelf";
import PropTypes from "prop-types";

const ListBooksContent = ({allBooks}) =>{
    let shelfName = ['Currently Reading','Want to Read','Read']; //"currentlyReading","read","wantToRead"
    const filterBook = (shelf) =>{
      allBooks.filter((book) => book.shelf === shelf);
    };
    let currentlyReadingBooks = () => {
      return allBooks.filter(filterBook('read'));
    };
    let wantToReadBooks = () => {
      return allBooks.filter(filterBook('wantToRead'));
    };
    let readBooks = () => {
      return allBooks.filter(filterBook('read'));
    };

    return (
      <div className="app">
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div>
            <BookShelf shelfName={shelfName[0]} shelfBooks={currentlyReadingBooks}></BookShelf>
            <BookShelf shelfName={shelfName[1]} shelfBooks={wantToReadBooks}></BookShelf>
            <BookShelf shelfName={shelfName[2]} shelfBooks={readBooks}></BookShelf>
          </div>
          <div className="open-search">
            <Link to="/search">Add a book</Link>
          </div>
        </div>
      </div>
    );
};

ListBooksContent.propTypes = {
  allBooks: PropTypes.array.isRequired
};

export default ListBooksContent;
