import { useState } from "react";
import { Link } from "react-router-dom";
import BooksGrid from "./BooksGrid";
import "../css/App.css";
import * as BooksAPI from "../utils/BooksAPI.js";

const SearchBooks = ({addNewBook}) =>{
  const[userInput,setUserInput] = useState("");
  const[searchMatchBook,setSearchMatchBook] = useState([]);

  const searchBook = async (bookStr) => {
         // let query = ""; await BooksAPI.search(query,100);    
          const res =  await BooksAPI.search(query,20);
          setSearchMatchBook(res);
  };

    return (
   <div className="search-books">
    <div className="search-books-bar">
      <div className="close-search">
        <Link to="/">Close</Link>
      </div>
      <div className="search-books-input-wrapper">
        <input
          type="text"
          value = {userInput}
          placeholder="Search by title, author, or ISBN"
          onChange={(event) => searchBook(event.target.value)}
        />
      </div>
    </div>
    <div>
    <BooksGrid shelfBooks={searchMatchBook}></BooksGrid>
    </div>
  </div>);
}

export default SearchBooks;
