import "./App.css";
import { useState } from "react";
import SearchBooks from "./SearchBooks.js";
import {Route,Routes,Router}  from "react-router-dom";
import BookShelf from "./BookShelf";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  
  return (
    <Router>
        <Routes>
            <Route
                exact
                path="/"
                element={
                  <BookShelf />
                }
              />
        </Routes>
      </Router>
  );
}

export default App;
