import { Link } from "react-router-dom";
import BookShelf from "./BookShelf";
import PropTypes from "prop-types";

const ListBooksContent = ({allBooks,setAllBooks}) =>{
    let shelfName = ['currentlyReading','read','wantToRead']; //"currentlyReading","read","wantToRead"

    const updateBook = (updatedBook) => {
      for(let i=0;i< allBooks.length ; i++){
        if(updateBook.bookId === allBooks[i].bookId){
          allBooks[i] = updateBook;
        }
      }
    };

    return (
      <div className="app">
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div>
            <BookShelf shelfName={shelfName[0]} allBooks={allBooks} updateBook={updateBook}></BookShelf>
            <BookShelf shelfName={shelfName[1]} allBooks={allBooks} updateBook={updateBook}></BookShelf>
            <BookShelf shelfName={shelfName[2]} allBooks={allBooks} updateBook={updateBook}></BookShelf>
          </div>
          <div className="open-search">
            <Link to="/search">Add a book</Link>
          </div>
        </div>
      </div>
    );
};

ListBooksContent.propTypes = {
  allBooks: PropTypes.array.isRequired
};

export default ListBooksContent;
