import { Link } from "react-router-dom";
import BookShelf from "./BookShelf";
import PropTypes from "prop-types";

const ListBooksContent = ({allBooks}) =>{
    let shelfNames = ['currentlyReading','read','wantToRead']; //"currentlyReading","read","wantToRead"

    return (
      <div className="app">
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div>
            {shelfNames.map((shelfName) => {
                <BookShelf shelfName={shelfName} allBooks={allBooks}></BookShelf>
            })}        
          </div>
          <div className="open-search">
            <Link to="/search">Add a book</Link>
          </div>
        </div>
      </div>
    );
};

ListBooksContent.propTypes = {
  allBooks: PropTypes.array.isRequired
};

export default ListBooksContent;
