import "../css/App.css";
import { useEffect, useState } from "react";
import SearchBooks from "./SearchBooks.js";
import ListBooksContent from "./ListBooksContent";
import * as BooksAPI from "../utils/BooksAPI.js";

function App() {
  const [allBooks,setAllBooks] = useState([]);
  const [showPage,setShowPage] = useState("list");

  useEffect(() => {
      const getAllBooks = async () => {
          const res = await BooksAPI.getAll();     
          setAllBooks(res);      
      };
      getAllBooks();
  },[]);


  const addOrUpdateBook = (bookIdVal,shelf) =>{
      var bookUpdated = false;
      allBooks.forEach((book) =>{
        if(bookIdVal === book.id){
            book.shelf = shelf;
            bookUpdated = true;
        }
      });

      if(!bookUpdated){
        const newBook = BooksAPI.get(bookIdVal);
        setAllBooks([...allBooks,newBook]);
      }else{
        setAllBooks([...allBooks]);
      }
  };

  const updatePageState = (pageVal) =>{
    setShowPage(pageVal);
  };


  return (
    <div>
        {
          showPage === "list" && <ListBooksContent allBooks={allBooks} addOrUpdateBook={addOrUpdateBook} updatePageState={updatePageState}/>
        }
        {
          showPage === "search" && <SearchBooks addOrUpdateBook={addOrUpdateBook} updatePageState={updatePageState}/>
        }
    </div>

  );
}

export default App;
