import { useState } from "react";
import BooksGrid from "./BooksGrid";
import "../css/App.css";
import * as BooksAPI from "../utils/BooksAPI.js";
import PropTypes from "prop-types";
import { useNavigate } from "react-router-dom";

const SearchBooks = ({addOrUpdateBook}) =>{
  const[searchMatchBook,setSearchMatchBook] = useState([]);
  let navigate = useNavigate();

  const searchBook = async (input) => {
          let query = input;
          if(input !== ""){
            const res =  await BooksAPI.search(query,20);   
            if(res !== null && res.error !== 'empty query'){
              setSearchMatchBook(res);
            }  
          }
  };

  const returnToHomePage = () =>{
    navigate("/");
  };

    return (
   <div className="search-books">
    <div className="search-books-bar">
      <div className="close-search">
            <div className="close-search" onClick={() =>returnToHomePage()}>
              Close
            </div>
      </div>
      <div className="search-books-input-wrapper">
        <input
          type="text"
          placeholder="Search by title, author, or ISBN"
          onChange={(event) => searchBook(event.target.value)}
        />
      </div>
    </div>
    <div>
    <BooksGrid shelfBooks={searchMatchBook} addOrUpdateBook={addOrUpdateBook}></BooksGrid>
    </div>
  </div>);
}

SearchBooks.propTypes = {
  addOrUpdateBook: PropTypes.func.isRequired
};

export default SearchBooks;
