import "../css/App.css";
import { useEffect, useState } from "react";
import SearchBooks from "./SearchBooks.js";
import {Route,Routes}  from "react-router-dom";
import ListBooksContent from "./ListBooksContent";
import * as BooksAPI from "../utils/BooksAPI.js";

function App() {
  const [allBooks,setAllBooks] = useState([]);

  useEffect(() => {
      const getAllBooks = async () => {
          const res = await BooksAPI.getAll();     
          setAllBooks(res);
          console.log('These are books' + allBooks);
      };
      getAllBooks();
  },[]);

  
  const addOrUpdateBook = (bookIdVal,shelf) =>{
      allBooks.forEach((book) =>{
        if(bookIdVal === book.id){
            book.shelf = shelf;
        }
      });
      setAllBooks([...allBooks]);
  };


  return (
    <Routes>
      <Route exact path="/" element={<ListBooksContent allBooks={allBooks} addOrUpdateBook={addOrUpdateBook}/>}/>
      <Route path="/search"
            element={
              <SearchBooks addOrUpdateBook={addOrUpdateBook}/>
            }/> 
    </Routes>

  );
}

export default App;
