import "../css/App.css";
import { useEffect, useState } from "react";
import SearchBooks from "./SearchBooks.js";
import {Route,Routes}  from "react-router-dom";
import ListBooksContent from "./ListBooksContent";
import * as BooksAPI from "../utils/BooksAPI.js";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [allBooks,setAllBooks] = useState([]);
  
  useEffect(() => {
      const getAllBooks = async() => {
          const res = BooksAPI.getAll();
          setAllBooks(res.data);
          console.log('List Books' + res.data);
      };
      getAllBooks();
  },[]);


  const addNewBook = (newBook) => {
    setAllBooks([...allBooks,newBook]);
  };

  return (
    <Routes>
      <Route exact path="/" element={<ListBooksContent allBooks={allBooks}/>}/>
      <Route path="/search"
            element={
              <SearchBooks showSearchPage={showSearchPage} setShowSearchpage={setShowSearchpage} addNewBook={addNewBook}/>
            }/> 
    </Routes>
  );
}

export default App;
